contracts/EscrowVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * Minimal USDC interface
 */
interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function transfer(address recipient, uint256 amount) external returns (bool);
}

/**
 * Minimal Warp Portal interface
 */
interface IWarpPortal {
    function messageToll() external view returns (uint256);
    function sendMessage(bytes3 _destination_chain, bytes32 _destination, bytes32[] calldata _contents) external payable;
}

/**
 * EscrowVault with oracle-based confirmation
 */
contract EscrowVault is AccessControl {
    // Role for the off-chain oracle (or set of oracles) that confirms cross-chain message receipt
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");

    IERC20 public usdc;
    IWarpPortal public portal;
    address public payoutAddress; // Where escrowed USDC is ultimately paid out once confirmed

    // HARDCODED Warp Destination puzzle hash on Chia
    bytes32 public constant HARDCODED_DESTINATION =
        0xfd94fc74376be3afa00e9c0e997c6fa52edcc7237e8305cbac305df7ee39644e; // Example

    // Chain ID for Chia: ASCII 'xch' => 0x786368
    bytes3 public constant CHIA_CHAIN_ID = 0x786368;

    // Access control: the contract deployer (msg.sender in constructor) is 'default admin'
    constructor(address _usdc, address _portal, address _payout) {
        usdc = IERC20(_usdc);
        portal = IWarpPortal(_portal);
        payoutAddress = _payout;

        // Grant the deployer the default admin role (can grant/revoke ORACLE_ROLE, etc.)
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    // Escrow record for each paymentId
    struct Escrow {
        address buyer;          // EVM address who paid
        uint256 amountUSDC;     // USDC escrowed
        bytes32 userPuzzleHash; // user's puzzle hash (where NFT goes on Chia)
        bytes32 nftId;          // 32-byte NFT ID
        uint256 dacCount;       // number of DACs (example use-case)
        bool completed;         // true if funds are released
        bytes32 warpNonce;      // we store the Warp nonce used for cross-chain message
    }

    // paymentId => Escrow
    mapping(bytes32 => Escrow) public escrows;

    // Emitted when user deposits
    event PaymentEscrowed(
        address indexed buyer,
        bytes32 indexed paymentId,
        uint256 amountUSDC,
        bytes32 userPuzzleHash,
        bytes32 nftId,
        uint256 dacCount,
        bytes32 warpNonce
    );

    // Emitted when escrow is released
    event EscrowReleased(
        bytes32 indexed paymentId,
        address payoutAddress,
        uint256 amountUSDC
    );

    /**
     * depositPayment:
     *   - Buyer calls with USDC + ETH toll.
     *   - Creates an Escrow record keyed by paymentId.
     *   - Calls warp portal with userPuzzleHash, nftId, paymentId, dacCount
     */
    function depositPayment(
        bytes32 nftId,
        bytes32 paymentId,
        bytes32 userPuzzleHash,
        uint256 amountUSDC,
        uint256 dacCount
    ) external payable {
        uint256 toll = portal.messageToll();
        require(msg.value == toll, "Must pay exact warp toll");
        require(paymentId != 0, "paymentId=0");
        require(userPuzzleHash != 0, "user puzzle hash=0");
        require(escrows[paymentId].amountUSDC == 0, "paymentId in use");

        // Transfer USDC in
        bool ok = usdc.transferFrom(msg.sender, address(this), amountUSDC);
        require(ok, "USDC transferFrom failed");

        // Store escrow
        escrows[paymentId] = Escrow({
            buyer: msg.sender,
            amountUSDC: amountUSDC,
            userPuzzleHash: userPuzzleHash,
            nftId: nftId,
            dacCount: dacCount,
            completed: false,
            warpNonce: bytes32(0) // set after we call sendMessage
        });

        // Prepare cross-chain message: userPuzzleHash, nftId, paymentId, dacCount
        bytes32[] memory contents = new bytes32[](4);
        contents[0] = userPuzzleHash;
        contents[1] = nftId;
        contents[2] = paymentId;
        contents[3] = bytes32(uint256(dacCount));

        // Warp the message to chain "xch" -> HARDCODED_DESTINATION
        // We can generate a custom nonce or rely on warp to do so. If warp exposes the nonce, store it.
        // For demonstration, we just store 'paymentId' as a reference or zero. In real usage, you might have to
        // read the 'nonce' from an event in an external script. Some warp versions return the nonce from the call.
        portal.sendMessage{ value: toll }(CHIA_CHAIN_ID, HARDCODED_DESTINATION, contents);

        // Optionally store a nonce if you have it. For now, we skip that step or store paymentId as a placeholder
        escrows[paymentId].warpNonce = paymentId; // not real but helps show correlation if needed

        // Fire event
        emit PaymentEscrowed(msg.sender, paymentId, amountUSDC, userPuzzleHash, nftId, dacCount, paymentId);
    }

    /**
     * confirmMessageReceived:
     *   - Called by your off-chain oracle once
     *     https://watcher-api.warp.green/messages?nonce=... shows 'status=received'
     *   - Must be called by an address with ORACLE_ROLE
     *   - Releases the escrowed USDC to payoutAddress
     */
    function confirmMessageReceived(
        bytes32 paymentId,
        string calldata warpTxHash // for reference, optional
    ) external onlyRole(ORACLE_ROLE) {
        Escrow storage esc = escrows[paymentId];
        require(!esc.completed, "Escrow already completed");
        require(esc.amountUSDC > 0, "Escrow not found or zero deposit");

        // Transfer escrow to final payout
        bool success = usdc.transfer(payoutAddress, esc.amountUSDC);
        require(success, "USDC transfer to payout failed");

        // Mark escrow completed
        esc.completed = true;

        emit EscrowReleased(paymentId, payoutAddress, esc.amountUSDC);
        // Optionally, also emit warpTxHash if you want to store cross-chain reference
    }

    /**
     * setOracle: lets the admin add or change which address can call confirmMessageReceived.
     */
    function setOracle(address newOracle) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(ORACLE_ROLE, newOracle);
    }

    /**
     * setPayout: update the final USDC payout address (admin only).
     */
    function setPayout(address newPayout) external onlyRole(DEFAULT_ADMIN_ROLE) {
        payoutAddress = newPayout;
    }
}

